import DefaultLayout from '@layout/default'
import Link from 'next/link'
import {getConfig, getAllPosts } from "pages/api"

export default function Blog(props) {
    return (
  <DefaultLayout title={props.title} descripction={props.description} >
      <h1>DianaIT's Blog</h1>
      <ul>
          {props.posts.map(function(post, idx) {
              return (
                  <li key={idx}>
                      üìù <Link href={'posts/'+ post.slug}>
              <a>{post.title}</a>
              </Link> <span style={{'float': 'right', 'padding-top': '15px'}}>  üìÖ {post.date}  </span>
                  </li>
              )
          })}
      </ul>
      <style jsx>
          {
              `
              ul {
                  list-style: none;
              }

              a {
                  text-decoration: none;
                  font-size: 2rem;
                  font-family: 'Amatic SC';
                  font-weight: bold;    
                 
        
              }

              li {
                  margin: 2rem;
                  padding-bottom: .5rem;
                  border-bottom: 1px solid lightgrey;
                  width: 100%;

        
                 
              }

              span {
                font-family: 'Poiret One';
                font-weight: bold;
              }
              `
          }
      </style>

  </DefaultLayout>
    )
}

export async function getStaticProps() {

    const config = await getConfig()
    const allPosts = await getAllPosts()

    return {
        props: {
            posts: allPosts,
            title: config.title,
            description: config.description
        }
    }
}
