export const shareTwitter = (tweet) => {
  const text = tweet.text;
  const url = tweet.url;
  const hashtags = tweet.hashtags;

  const textToURL = text.replace(" ", "%20");

  return `https://twitter.com/intent/tweet?text=${textToURL}&url=${url}&hashtags=${hashtags}`;
};
