export const shareTwitter = (tweet) => {
  console.log(tweet);
  const text = tweet.text | "ajdkfasdjfkl";
  const url = tweet.url;
  const hashtags = tweet.hashtags;

  //const textToURL = text.replace(" ", "%20") | "kasjdflkajdfk";

  return `https://twitter.com/intent/tweet?text=${text}&url=${url}&hashtags=${hashtags}`;
};
