import Link from 'next/link'
import DefaultLayout from '@layout/default'

export default function Home() {
  return (
    <>
<DefaultLayout>
 <section>
 <nav>
            <a href='https://twitter.com/dianait_'><img src='https://cdn.icon-icons.com/icons2/1183/PNG/512/1490133460-social-icons01_82210.png' /></a>
            <a href='https://www.linkedin.com/in/dianahernandezsoler/'><img src='https://image.flaticon.com/icons/png/512/174/174857.png' /></a>
            <a href='https://github.com/dianait'><img src='https://cdn2.iconfinder.com/data/icons/social-icons-grey/512/GITHUB-512.png' /></a>
            <a href='https://codepen.io/dashboard/'><img src='https://image.flaticon.com/icons/svg/2111/2111262.svg' /></a>
        </nav>
<img className='avatar' src='./img/avatar.jpg' alt='diana avatar'/>
</section>
<section>
        <h1>
        <Link href='/'>
        <a  >   Diana Hernández </a>
        </Link>
        
        </h1>
        <h2>Desarrollo web</h2>
   
        <Link href='/blog'>
        <a className='getStarted' > Get started!</a>
        </Link>
        </section>
      
</DefaultLayout>
     

    <style jsx>{`

@import url('https://fonts.googleapis.com/css2?family=Amatic+SC:wght@400;700&family=Dancing+Script&family=Poiret+One&display=swap');
DefaultLayout, main {

  justify-content: center !important;
  
}
h1 {
  font-family: 'Amatic SC', cursive;
  font-size: 4rem;
  margin: 0;

}

h2 {
  font-family: 'Poiret One', cursive;
  letter-spacing: 12px;
margin-top: -.8rem;}

  section {
    display:flex;
  }

  section:nth-child(2n) {
    flex-direction: column;
  }
nav {
  display: flex;
  flex-direction: column;
  margin: auto auto;
  padding-right: .8rem;
  border-right: 2px solid lightgrey;

}
img {
  width: 30px;
  height: 30px;
}

a {
  text-align:center;
  text-decoration:none; 
}

.avatar{
        width:200px;
        height:200px;
        border-radius:100px;
        margin-right:2.5rem;
        margin-bottom:1rem;
        margin-left: 1rem;
      }
      .getStarted {
        box-shadow:inset 0px 1px 0px 0px #fce2c1;
        background:linear-gradient(to bottom, #ffc477 5%, #fb9e25 100%);
        background-color:#ffc477;
        border-radius:6px;
        border:1px solid #eeb44f;
        display:inline-block;
        cursor:pointer;
        color:#ffffff;
        font-size:15px;
        font-weight:bold;
        padding:6px 24px;
        text-decoration:none;
        text-shadow:0px 1px 0px #cc9f52;
        display: flex;
        flex-direction: column;
      }
      .getStarted:hover {
        background:linear-gradient(to bottom, #fb9e25 5%, #ffc477 100%);
        background-color:#fb9e25;
      }
      .getStarted:active {
        position:relative;
        top:1px;
      }
      
      `

      }
</style>
    </>
  )
}
